import React from 'react';
import ReactDOM from 'react-dom';

//liの初期化。liには各ステータスの星の個数(整数値)が格納される
let li = [0];

function buttonClicked(props){
  if(props.d === "buttom"){
    li.push(0);
  }else if(props.d === "top"){
    li.pop();
  }else if(props.d === "right"){
    li[li.length-1] += 1;
  }else if(props.d === "left"){
    li[li.length-1] -= 1;
  }
}

function oneStatus(props){
  const stars = (n) =>{
    if(n > 0){
      return "★"+stars(n-1);
    }else{
      return "";
    }
  };
  return (
    <tr><td>sss</td></tr>
  );
}

function func() {
  let element = (
    <div>
      <table border="0">
        {li.map(n => <oneStatus n={n} />)}
      </table>
    </div>
  );
  //後のために、ここで直接ReactDOM.render使うことにする
  ReactDOM.render(
    element,
    document.getElementById('root')
  );
  return null;
}

setInterval(func, 100);

//とりあえずnullにしておく
export default null;
