import React from 'react';
import ReactDOM from 'react-dom';

//liの初期化。liには各ステータスの星の個数(整数値)が格納される
//map()によりkeyをリストの要素番号にしてる
let li = [0];

function buttonClicked(d,key){
  if(d === "buttom"){
    li.push(0);
  }else if(d === "top"){
    li.pop();
  }else if(d == "right"){
    li[key] += 1;
  }else if(d === "left"){
    li[key] -= 1;
  }
  return null;
}

function OneStatus(props){
  const stars = (n) =>{
    if(n > 0){
      return "★"+stars(n-1);
    }else{
      return "";
    }
  };
  return (
    <tr>
      <td><button onClick={() => buttonClicked("left",props.myKey)}>←</button ></td>
      <td><button onClick={() => buttonClicked("right",props.myKey)}>→</button ></td>
      <td><input id={"statusName_"+props.myKey} type="text" /></td>
      <td id={"statusN_"+props.myKey}>{stars(props.n)}</td>
    </tr>
  );
}

function myForm() {
  let element = (
    <div>
      <div>
        <h1>入力フォーム</h1>
        <p>ステータス名を入力して強さ(星)を「←」「→」で選んでね<br/>「↑」「↓」で新しいステータスを追加・削除できるよ</p>
        <br/>
        <p><label>おなまえ: </label><input id="name" type="text" value="a" /></p>
        <table border="0">
          <tbody>
            {li.map((n,key) => <OneStatus n={n} key={key} myKey={key}/>)}
          </tbody>
        </table>
        <br/>
        <table border="0">
          <tbody>
            <tr>
              <td><button onClick={() => buttonClicked("top")}>↑</button></td>
              <td><button onClick={() => buttonClicked("buttom")}>↓</button></td>
            </tr>
          </tbody>
        </table>
      </div>
    </div>
  );
  //func()のelementにある<div id="myForm"></div>にrenderする
  ReactDOM.render(
    element,
    document.getElementById('myForm')
  );
  return null;
}

function myResult(){
  const NameAndStars = (props) => {
    const nameElement = document.getElementById("statusName_"+props.key);
    const starsElement = document.getElementById("statusN_"+props.key);
    console.log(nameElement)
    return (
      <tr>
        <td>{nameElement.value}</td>
        <td>{starsElement.value}</td>
      </tr>
    );
  }
  let element = (
    <div>
      <h1>あなたのステータス</h1>
      <p><label>おなまえ: </label>{document.getElementById("name").value}</p>
      <table border="0">
        <tbody>
          <tr><td>aaaaaaaaaaaaaa</td></tr>
          {li.map((n,key) => <NameAndStars myKey={key} />)}
        </tbody>
      </table>
    </div>
  );
  ReactDOM.render(
    element,
    document.getElementById('myResult')
  );
}

function func(){
  let element = (
    <div>
      <div id="myForm"></div>
      <br /><br /><h1>↓↓↓</h1><br /><br />
      <div id="myResult"></div>
    </div>
  );
  //直接ReactDOM.renderを使う
  ReactDOM.render(
    element,
    document.getElementById('root')
  );
  myForm();
  myResult();
}

setInterval(func, 1000);

//とりあえずnullにしておく
export default null;
