import React from 'react';
import ReactDOM from 'react-dom';

//liの初期化。liには各ステータスの星の個数(整数値)が格納される
//map()によりkeyをリストの要素番号にしてる
let li = [0];

function buttonClicked(d,key){
  if(d === "buttom"){
    li.push(0);
  }else if(d === "top"){
    li.pop();
  }else if(d == "right"){
    li[key] += 1;
  }else if(d === "left"){
    li[key] -= 1;
  }
  return null;
}

function OneStatus(props){
  const stars = (n) =>{
    if(n > 0){
      return "★"+stars(n-1);
    }else{
      return "";
    }
  };
  return (
    <tr>
      <td><button onClick={() => buttonClicked("left",props.myKey)}>←</button ></td>
      <td><button onClick={() => buttonClicked("right",props.myKey)}>→</button ></td>
      <td><input id={"status_"+props.myKey} type="text" /></td>
      <td id={"n_"+props.myKey}>{stars(props.n)}</td>
    </tr>
  );
}



function myForm(){
  let element = (
    <div>
      <div>
        <h1>入力フォーム</h1>
        <p>ステータス名を入力して強さ(星)を「←」「→」で選んでね<br/>「↑」「↓」で新しいステータスを追加・削除できるよ</p>
        <br/>
        <p><label>おなまえ: </label><input id="name" type="text" value="a" /></p>
        <table border="0">
          <tbody>
            {li.map((n,key) => <OneStatus n={n} key={key} myKey={key}/>)}
          </tbody>
        </table>
        <br/>
        <table border="0">
          <tbody>
            <tr>
              <td><button onClick={() => buttonClicked("top")}>↑</button></td>
              <td><button onClick={() => buttonClicked("buttom")}>↓</button></td>
            </tr>
          </tbody>
        </table>
      </div>
    </div>
  );
  return element;
}

function func() {
  let element = (
    <myForm />
  );
  //後のために、ここで直接ReactDOM.render使うことにする
  ReactDOM.render(
    element,
    document.getElementById('root')
  );

  return null;
}

setInterval(func, 1000);

//とりあえずnullにしておく
export default null;
