import React from 'react';
import ReactDOM from 'react-dom';

/*
  (重要)ReactDOMを先頭に付けないと画面を更新してくれない
  index.jsでReactDOM.renderを使っているが、各importされる側の.jsでもやる必要あり
  (超重要)関数内でrenderしてるなら要素をreturnする必要が無い
    クラスを使わず関数コンポーネントで記述するなら尚更大事？
  その他:
    renderをindex.jsとほかの.jsで合計2重になるように使うとエラーが出る
*/

function tick() {
  let element = (
    <div>
      <h2>It is {new Date().toLocaleTimeString()} !!!!+!!!!!!!</h2>
      <br />
    </div>
  );
  ReactDOM.render(
    element,
    document.getElementById('root')
  );
  return null;
}

setInterval(tick, 1000);

tick();
