import React from 'react';
import ReactDOM from 'react-dom';

//liの初期化。liには各ステータスの星の個数(整数値)が格納される
let li = [0];

function buttonClicked(d){
  if(d === "buttom"){
    li.push(0);
  }else if(d === "top"){
    li.pop();
  }else if(d == "right"){
    li[li.length-1] += 1;
  }else if(d === "left"){
    li[li.length-1] -= 1;
  }
}

function OneStatus(props){
  const stars = (n) =>{
    if(n > 0){
      return "★"+stars(n-1);
    }else{
      return "";
    }
  };
  return (
    <tr>
      <td><h2><button onClick={() => buttonClicked("left")}>←</button ></h2></td>
      <td><h2><button onClick={() => buttonClicked("right")}>→</button ></h2></td>
      <td><h2><input type="text"></input></h2></td>
      <td><h2>{stars(props.n)}</h2></td>
    </tr>
  );
}

function func() {
  let element = (
    <div>
      <table border="0">
        {li.map(n => <OneStatus n={n} />)}
        <td><h2><button onClick={() => buttonClicked("top")}>↑</button ></h2></td>
        <td><h2><button onClick={() => buttonClicked("buttom")}>↓</button ></h2></td>
      </table>
    </div>
  );
  //後のために、ここで直接ReactDOM.render使うことにする
  ReactDOM.render(
    element,
    document.getElementById('root')
  );
  return null;
}

setInterval(func, 10);

//とりあえずnullにしておく
export default null;
