import React from 'react';
import ReactDOM from 'react-dom';

//liの初期化。liには各ステータスの星の個数(整数値)が格納される
let li = [0];

function buttonClicked(d){
  if(d === "buttom"){
    li.push(0);
  }else if(d === "top"){
    li.pop();
  }else if(d == "right"){
    li[li.length-1] += 1;
  }else if(d === "left"){
    li[li.length-1] -= 1;
  }
}

function OneStatus(props){
  const stars = (n) =>{
    if(n > 0){
      return "★"+stars(n-1);
    }else{
      return "";
    }
  };
  return (
    <tr>
      <td><button onClick={() => buttonClicked("left")}>←</button ></td>
      <td><button onClick={() => buttonClicked("right")}>→</button ></td>
      <td><input type="text"></input></td>
      <td>{stars(props.n)}</td>
    </tr>
  );
}

function func() {
  let element = (
    <div>
      <h1>あなたのステータス</h1>
      <p>ステータス名を入力して強さ(星)を「←」「→」で選んでね<br/>「↑」「↓」で新しいステータスを追加・削除できるよ</p>
      <br/>
      <label>おなまえ: </label><input type="text"></input><br/>
      <table border="0">
        {li.map(n => <OneStatus n={n} />)}
        <br />
        <tr>
          <td><button onClick={() => buttonClicked("top")}>↑</button ></td>
          <td><button onClick={() => buttonClicked("buttom")}>↓</button ></td>
          <td></td>
          <td></td>
        </tr>
      </table>
    </div>
  );
  //後のために、ここで直接ReactDOM.render使うことにする
  ReactDOM.render(
    element,
    document.getElementById('root')
  );
  return null;
}

setInterval(func, 10);

//とりあえずnullにしておく
export default null;
