import React from 'react';
import ReactDOM from 'react-dom';

//liの初期化。liには各ステータスの星の個数(整数値)が格納される
let li = [{"key":{"n":0}}];

function buttonClicked(d,key){
  if(d === "buttom"){
    //ここはlength-1にしない
    li.push({"key":li.length, "n":0});
  }else if(d === "top"){
    li.pop();
  }else if(d == "right"){
    li[key] += 1;
  }else if(d === "left"){
    li[key] -= 1;
  }
}

function OneStatus(props){
  const stars = (n) =>{
    if(n > 0){
      return "★"+stars(n-1);
    }else{
      return "";
    }
  };
  return (
    <tr>
      <td><button onClick={() => buttonClicked("left",props.key)}>←</button ></td>
      <td><button onClick={() => buttonClicked("right",props.key)}>→</button ></td>
      <td><input type="text"></input></td>
      <td>{stars(props.n)}</td>
    </tr>
  );
}

function func() {
  let element = (
    <div>
      <h1>あなたのステータス</h1>
      <h1>{li.key.n}</h1>
      <p>ステータス名を入力して強さ(星)を「←」「→」で選んでね<br/>「↑」「↓」で新しいステータスを追加・削除できるよ</p>
      <br/>
      <p><label>おなまえ: </label><input type="text"></input></p>
      <br/>
      <table border="0">
        <tbody>
          {li.map((key,n) => <OneStatus key={key} n={n}/>)}
        </tbody>
      </table>
      <br/>
      <table border="0">
        <tbody>
          <tr>
            <td><button onClick={() => buttonClicked("top")}>↑</button></td>
            <td><button onClick={() => buttonClicked("buttom")}>↓</button></td>
          </tr>
        </tbody>
      </table>
    </div>
  );
  //後のために、ここで直接ReactDOM.render使うことにする
  ReactDOM.render(
    element,
    document.getElementById('root')
  );
  return null;
}

setInterval(func, 1000);

//とりあえずnullにしておく
export default null;
